// src/pages/HomePage.js
import React, {useEffect} from 'react';
import { Link } from 'react-router-dom';
import { checkAuthentication, logoutUser } from './auth';


  useEffect(() => {
    const fetchAuthStatus = async () => {
      const userEmail = await checkAuthentication();
      if (userEmail) {
        setIsAuthenticated(true);
      }
    };
    fetchAuthStatus();
  }, []);

  const handleLogout = async () => {
    const success = await logoutUser();
    if (success) {
      setIsAuthenticated(false);
    }
  };

const HomePage = () => {
  return (
    <div className="home-page">
      <h1>Welcome to the Travel App</h1>
      <div>
        <Link to="/search">
          <button className="btn btn-primary">Search Flights</button>
        </Link>
        <Link to="/hotel">
          <button className="btn btn-primary">Search Hotels</button>
        </Link>
        <Link to="/login">
          <button className="btn btn-secondary">Login</button>
        </Link>
        <Link to="/registration">
          <button className="btn btn-secondary">Register</button>
        </Link>
      </div>
    </div>
  );
};

export default HomePage;
